import { Col, Modal, Row, Spin, Table, Tabs, Space, Card, Upload, Image, Tag, List, Empty } from "antd";
import { useState } from "react";
import { noData } from "utils/verification";
import { Type } from "views/home/request";
import { useHistoryModal } from '../util'
const { TabPane } = Tabs;
const baseUrl = process.env["REACT_APP_API_URL"]
export const ModalForm = () => {
  const { ModalOpen, planHistory, close, isLoading } = useHistoryModal()
  const [visible, setVisible] = useState({
    show: false,
    photoList: []
  });
  const [bodyStyle] = useState({ width: "100%", height: "100%", overflow: "hidden" })
  function callback(key: string) {
    console.log(key);
  }

  const props = {
    defaultFileList: planHistory?.data.documentList
  };

  const status = (id: number) => {
    switch (id) {
      case 0:
        return <Tag color="processing">无状态</Tag>
      case 1:
        return <Tag color="success" >已完成</Tag>
      case 2:
        return <Tag color="error">未完成</Tag>
      default:
        break
    }
  }

  return (
    <Modal
      title={"作业历史"}
      width={1200}
      visible={ModalOpen}
      onCancel={close}
      footer={null}>
      {
        isLoading ? (
          <Spin size={"large"} />
        ) : (
          <>
            <Row>
              <Col style={mb} span={8}>计划名称：{planHistory?.data.name}</Col>
              <Col span={8}>作业部门：{planHistory?.data.departmentName}</Col>

              <Col style={mb} span={8}>施工负责人：{planHistory?.data.leaderName}</Col>
              <Col span={8}>线路：{planHistory?.data.lineName}</Col>

              <Col style={mb} span={8}>作业区域：{planHistory?.data.workAddr}</Col>
              <Col span={8}>请站点：{planHistory?.data.pleaseName}</Col>

              <Col style={mb} span={8}>销站点：{planHistory?.data.pinName}</Col>
              <Col span={8}>作业类型：{planHistory?.data.workAddr}</Col>

              <Col style={mb} span={8}>开始时间：{planHistory?.data.beginTime}</Col>
              <Col span={8}>结束时间：{planHistory?.data.endTime}</Col>

              <Col style={mb} span={8}>提醒时间：{planHistory?.data.warnTime || "无"}</Col>
              <Col span={8}>作业日期：{planHistory?.data.dateTime || "无"}</Col>

              <Col style={mb} span={8}>是否自动提醒：{planHistory?.data.isWarn === 0 ? "否" : "是"}</Col>
              <Col span={8}>作业人数：{planHistory?.data.workPerson || "无"}</Col>

              <Col style={mb} span={8}>作业内容：{planHistory?.data.workContent || "无"}</Col>
              <Col span={8}>计划令号：{planHistory?.data.num || "无"}</Col>

              <Col span={8}>
                文档：
                <Upload {...props} style={{ width: "100%" }}>
                </Upload>
              </Col>
              <Col span={8}>备注：{planHistory?.data.remark || "无"}</Col>
            </Row>

            <Tabs defaultActiveKey="1" onChange={callback} size={"small"}>
              <TabPane tab="养护小组" key="1">
                <Space direction="vertical" style={{ width: "100%" }}>
                  {
                    planHistory?.data.webGroupList.length > 0 && planHistory?.data.webGroupList ? (
                      planHistory?.data.webGroupList.map((item: any) => (
                        <Card title={<Tag color="#2db7f5">{item.groupName}</Tag>} extra={<><Tag color="geekblue">小组成员</Tag>{item.personList.map((key: any) => (<span>{key.personName}，</span>))}</>} style={{ width: "100%" }}>
                          <Space direction="horizontal" style={{ width: "100%" }}>
                            <Card
                              title={"计划需要的工具"}
                              bodyStyle={bodyStyle}
                            >
                              {
                                item.planToolList.map((key: any) => (
                                  <p style={{ display: "flex", justifyContent: "space-between" }}>
                                    <span>{key.name}</span>
                                    <span>{key.num}</span>
                                  </p>
                                ))
                              }
                            </Card>

                            <Card
                              title={"计划需要的物料"}
                              bodyStyle={bodyStyle}
                            >
                              {
                                item.planMaterialList.map((key: any) => (
                                  <p style={{ display: "flex", justifyContent: "space-between" }}>
                                    <span>{key.name}</span>
                                    <span>{key.num}</span>
                                  </p>
                                ))
                              }
                            </Card>

                            <Card
                              title={"领用归还的工具"}
                              bodyStyle={bodyStyle}
                            >
                              {
                                item.groupToolList.map((key: any) => (
                                  <p style={{ display: "flex", justifyContent: "space-between" }}>
                                    <span>{key.name}</span>
                                    <span>{key.num}</span>
                                  </p>))
                              }
                            </Card>

                            <Card
                              title={"领用归还的未绑定标签"}
                              bodyStyle={bodyStyle}
                            >
                              {
                                item.groupMaterialList.map((key: any) => (
                                  <p style={{ display: "flex", justifyContent: "space-between" }}>
                                    <span>{key.name}</span>
                                    <span>{key.num}</span>
                                  </p>))
                              }
                            </Card>

                            <Card
                              title={"拾取的工具材料"}
                              bodyStyle={bodyStyle}
                            >
                              {
                                item.legacyToolList.map((key: any) => (
                                  <p style={{ display: "flex", justifyContent: "space-between" }}>
                                    <span>{key.name}</span>
                                    <span>{key.num}</span>
                                  </p>))
                              }
                            </Card>

                            <Card
                              title={"清点归还拍照"}
                              bodyStyle={bodyStyle}
                            >
                              <div onClick={() => {
                                setVisible({ ...visible, show: true, photoList: item.photoList })
                              }}>
                                {
                                  console.log(item.photoList, item.photoList[0])
                                }
                                <Image width={"100%"} height={"100%"} preview={false} src={item.photoList.length > 0 && item.photoList ? `${baseUrl}file/perview/${item.photoList[0]}` : ""} alt=""
                                  fallback="data:image/png;base64,"
                                />
                              </div>
                            </Card>
                          </Space>
                        </Card>
                      ))
                    ) : (
                      <Empty description={<div>暂无数据</div>} />
                    )

                  }
                </Space>
              </TabPane>
              <TabPane tab="签到人员" key="2">
                <Table
                  pagination={false}
                  columns={[
                    {
                      title: "签到人员",
                      dataIndex: "personName"
                    },
                    {
                      title: "签到时间",
                      dataIndex: "createTime"
                    }
                  ]}
                  dataSource={planHistory?.data.registrationList}
                  locale={noData}
                  rowKey={(item: any) => item.id}
                />
              </TabPane>

              <TabPane tab="告警记录" key="3">
                <Table
                  pagination={false}
                  columns={[
                    {
                      title: "告警类型",
                      render: (item: any) => <>{Type(item.type)}</>
                    },
                    {
                      title: "小组名称",
                      dataIndex: "groupName"
                    },
                    {
                      title: "时间",
                      dataIndex: "warnTime"
                    },
                    {
                      title: "告警内容",
                      dataIndex: "content"
                    }
                  ]}
                  dataSource={planHistory?.data.warnList}
                  rowKey={(item: any) => item.id}
                  locale={noData}
                />
              </TabPane>

              <TabPane tab="工场清单及签字图片" key="4">
                <List>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepA)
                    }
                    所有参与人员提前一小时到达车站
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepB)
                    }
                    作业分工
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepC)
                    }
                    开展安全交底
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepD)
                    }
                    所有参与作业人员进行安全交底确认签字
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepE)
                    }
                    安全交底信息及影像反馈
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepF)
                    }
                    工器具及材料清点人清点材料工具并进行登记、签名
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepG)
                    }
                    工器具及材料确认人复核清点材料工具并签名
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepH)
                    }
                    工器具及材料清点信息及影像反馈
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepI)
                    }
                    设置工器具及材料摆放点
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepJ)
                    }
                    确认作业过程中是否有新增发现的材料、工器具、遗留物等情况
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepK)
                    }
                    提前半小时进行出清
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepL)
                    }
                    检查作业覆盖区域
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepM)
                    }
                    位于撤离队伍后方进行最后检查把关
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepN)
                    }
                    是否有原属于轨行区物件需出清到轨行区外，确保在清点清单中登记确认
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepO)
                    }
                    工器具及材料清点人清点出清工具及材料、签名
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepP)
                    }
                    工器具及材料清点人复核清点材料工器具出清情况并签名
                  </List.Item>
                  <List.Item>
                    {
                      status(planHistory?.data.signature.stepQ)
                    }
                    工器具及材料清点信息及影像反馈
                  </List.Item>

                </List>
                <Space direction="horizontal" style={{ width: "100%" }}>
                  <Card title={"施工负责人电子签名"} bodyStyle={bodyStyle}>
                    <Image width={"100%"} src={`${baseUrl}file/perview/${planHistory?.data.signature?.leaderSignature}`}
                      fallback="data:image/png;base64,"
                    />
                  </Card>
                  <Card title={"安全员电子签名"} bodyStyle={bodyStyle}>
                    <Image width={"100%"} src={`${baseUrl}file/perview/${planHistory?.data.signature.safeSignature}`}
                      fallback="data:image/png;base64,"
                    />
                  </Card>
                  <Card title={"清点人电子签名"} bodyStyle={bodyStyle}>
                    <Image width={"100%"} src={`${baseUrl}file/perview/${planHistory?.data.signature.countSignature}`}
                      fallback="data:image/png;base64,"
                    />
                  </Card>
                  <Card title={"出清负责人电子签名"} bodyStyle={bodyStyle}>
                    <Image width={"100%"} src={`${baseUrl}file/perview/${planHistory?.data.signature.clearSignature}`}
                      fallback="data:image/png;base64,"
                    />
                  </Card>
                  <Card title={"确认人电子签名"} bodyStyle={bodyStyle}>
                    <Image width={"100%"} src={`${baseUrl}file/perview/${planHistory?.data.signature.confirmSignature}`}
                      fallback="data:image/png;base64,"
                    />
                  </Card>
                </Space>
              </TabPane>
            </Tabs>

          </>
        )
      }

      <Modal
        title={"清点归还图片"}
        width={1200}
        visible={visible.show}
        footer={false}
        onCancel={() => setVisible({ ...visible, show: false })}
      >
        <Image.PreviewGroup>
          {
            visible.photoList.map((item: any) => <Image width={100} src={`${baseUrl}file/perview/${item}`}
              fallback="data:image/png;base64,"
            />)
          }
        </Image.PreviewGroup>
      </Modal>
    </Modal>
  )
}

const mb = { marginBottom: "1rem" }
