const LineTitle = styled.h1`
  display: flex;
  align-items: center;
  .title {
    font-size: 16px;
    font-weight: bold;
    color: #1b81ff;
    margin-right: 10px;
  }
`;

const GroupList = styled.div`
  display: flex;
  // padding: 5px;
  border: 1px solid #eee;
  justify-content: space-around;
  flex-direction: row;
  flex-wrap: wrap;
  margin-bottom: 20px;
  > * {
    width: 22%;
    margin: 20px 0;
  }
`;
